import { useState } from "react";
import { Box, Image, Text, SimpleGrid, Heading } from "@chakra-ui/react";

function Card() {
  const [isHovered, setIsHovered] = useState(false);
  let CountryData = [
    {
      title: "Aagra",
      image:
        "data:image/jpeg;base64,",
    },{
      title:"Mumbai",
      image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTCi6Zvne1hTu4Fq2QUwU4ND8tGvt0ksbkBmg&usqp=CAU"
    },
    {
      title:"India Gate",
      image:"data:image/jpeg;base64,"
    },{
      title:"Akshardham",
      image:"data:image/jpeg;base64,"
    },{
      title:"Lal kila",
      image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTPLuNAVNmKJEb0xIjMFXyjYTbp-t769U-xgg&usqp=CAU"
    },
    {
      title:"Lotus Temple",
      image:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRwkWz_xEHhLTsVlXHiBYgSXLKpQrQbO4ELuw&usqp=CAU"
    },{
      title:"Qutub Minar",
      image:"data:image/jpeg;base64,"
    },{
      title:"Jantar Mantar",
      image:"https://res.cloudinary.com/thrillophilia/image/upload/c_fill,f_auto,fl_progressive.strip_profile,g_center,h_230,q_auto,w_305/v1/filestore/jdba4ix0vx6b6b4yzmbo2qv3xist_shutterstock_539091853.jpg"
    }
  ];

  return (
    <>
    <Box bgColor={"gray.100"}>
    <Heading color="gold">TAKE A LOOK AT OUR MOST POPULAR TOURS</Heading>
    </Box>
      <SimpleGrid columns={[1, 2, 4]} spacing={4} mt={8} w="100%">
        
        {CountryData &&
          CountryData.map((t) => {
            return (
              <Box
                key={t.title}
                position="relative"
                onMouseEnter={() => setIsHovered(t.title)}
                onMouseLeave={() => setIsHovered(false)}
              >
                <Image
                  src={t.image}
                  alt="Placeholder image"
                  h="200px"
                  w="100%"
                  objectFit="cover"
                  opacity={!isHovered || isHovered !== t.title ? 1 : 0.8}
                  transition="opacity 0.3s ease-in-out"
                />
                {isHovered === t.title && (
                  <Box
                    position="absolute"
                    top="50%"
                    left="50%"
                    transform="translate(-50%, -50%)"
                    bg="rgba(0, 0, 0, 0.5)"
                    display="flex"
                    alignItems="center"
                    justifyContent="center"
                    h="100%"
                    w="100%"
                  >
                    <Text color="white" fontWeight="bold" fontSize="2xl">
                      {t.title}
                    </Text>
                  </Box>
                )}
              </Box>
            );
          })}
      </SimpleGrid>
    </>
  );
}

export default Card;
